{#if ['area', 'base', 'br', 'col', 'embed', 'hr', 'img', 'input', 'link', 'meta', 'param', 'source', 'track', 'wbr'].indexOf(tag) > -1}
  <svelte:element
    this={tag}
    bind:this={element}
    use:useActions={use}
    use:forwardEvents
    {...$$restProps}
  />
{:else}
  <svelte:element
    this={tag}
    bind:this={element}
    use:useActions={use}
    use:forwardEvents
    {...$$restProps}><slot /></svelte:element
  >
{/if}

<script>import { get_current_component } from 'svelte/internal';
import { forwardEventsBuilder, useActions } from './internal/index.js';
// Remember to update types file if you add/remove/rename props.
export let use = [];
export let tag = 'div';
const forwardEvents = forwardEventsBuilder(get_current_component());
let element;
export function getElement() {
    return element;
}
</script>
